package java0822_statement;
/*
 * 		if(조건식){
 * 			수행할 문장;//조건식이 참일때 수행.
 * 		}else{
 * 			수행할 문장;//조건식이 거짓일때 수행.
 * 		}
 */

public class Java018_if {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = -10;
		
		if(num>0) {
			System.out.printf("%d 는 자연수입니다. ",num);
			
		}
		
		System.out.println("program end " );
		
		

	}

}
